  border-radius: 6px;
  height: fit-content;
}

.sidebar h2 {
  font-size: 14px;
  font-weight: 600;
  margin: 0;
  padding: 16px;
  background: var(--color-canvas-subtle);
  border-bottom: 1px solid var(--color-border-default);
  color: var(--color-fg-default);
}

.blog-list {
  padding: 8px;
  display: flex;
  flex-direction: column;
  gap: 4px;
}

.blog-item.active {
  background: var(--color-canvas-subtle);
  padding: 8px 12px;
  border-radius: 6px;
  border: 1px solid var(--color-border-default);
}

.blog-title {
  font-weight: 600;
  color: var(--color-accent-fg);
  text-decoration: none;
  word-wrap: break-word;
  display: block;
  font-size: 14px;
  line-height: 1.25;
}

.blog-title:hover {
  text-decoration: underline;
  color: var(--color-accent-fg);
}

.toggle-btn {
  background: none;
  border: none;
  cursor: pointer;
  float: right;
  color: var(--color-fg-muted);
