}

.sidebar-note a:hover {
  text-decoration: underline;
}

/* ===== Main Content ===== */
.main-content {
  flex: 1;
  padding: 0;
  background: var(--color-canvas-default);
  overflow-x: hidden;
  word-wrap: break-word;
}

.blog-content {
  max-width: none;
}

.blog-content h1 {
  font-size: 32px;
