}

.content-html table {
  border-spacing: 0;
  border-collapse: collapse;
  display: block;
  width: max-content;
  max-width: 100%;
  overflow: auto;
  margin: 16px 0;
}

.content-html table th {
  font-weight: 600;
  padding: 6px 13px;
  border: 1px solid var(--color-border-default);
  background: var(--color-canvas-subtle);
  color: var(--color-fg-default);
}

.content-html table td {
  padding: 6px 13px;
  border: 1px solid var(--color-border-default);
  color: var(--color-fg-default);
}

.content-html table tr {
  background-color: var(--color-canvas-default);
  border-top: 1px solid var(--color-border-muted);
}

.content-html table tr:nth-child(2n) {
  background-color: var(--color-canvas-subtle);
}

/* Inline code styling - GitHub Dark exact */
.content-html code:not(pre code) {
  background: var(--color-neutral-muted);
  color: var(--color-fg-default);
  font-family: ui-monospace, SFMono-Regular, "SF Mono", Menlo, Consolas, "Liberation Mono", monospace;
  padding: 2px 4px;
  border-radius: 6px;
  font-size: 85%;
  margin: 0;
}

/* Code blocks styling - GitHub Dark exact */
.content-html pre {
  background: var(--color-canvas-subtle);
  color: var(--color-fg-default);
  padding: 16px;
  border-radius: 6px;
  font-family: ui-monospace, SFMono-Regular, "SF Mono", Menlo, Consolas, "Liberation Mono", monospace;
  font-size: 13px;
  overflow-x: auto;
  line-height: 1.45;
  margin: 16px 0;
  border: 1px solid var(--color-border-default);
}

.content-html pre code {
  background: none;
  padding: 0;
  border-radius: 0;
  font-size: 100%;
  color: var(--color-fg-default);
}

/* Code block with language label */
.code-block {
  position: relative;
  margin: 16px 0;
}

.language-label {
  position: absolute;
  top: 8px;
  right: 8px;
  background: rgba(48, 54, 61, 0.9);
  color: var(--color-fg-muted);
  padding: 2px 8px;
  font-size: 11px;
  border-radius: 6px;
  font-family: ui-monospace, SFMono-Regular, "SF Mono", Menlo, Consolas, "Liberation Mono", monospace;
  text-transform: uppercase;
  font-weight: 600;
  border: 1px solid var(--color-border-default);
  letter-spacing: 0.5px;
  backdrop-filter: blur(4px);
}

/* Links - GitHub Dark exact */
.content-html a {
  color: var(--color-accent-fg);
  text-decoration: none;
}

.content-html a:hover {
  text-decoration: underline;
  color: var(--color-accent-fg);
}

/* GitHub Dark style horizontal rule */
.content-html hr {
  height: 1px;
  padding: 0;
  margin: 24px 0;
  background-color: var(--color-border-default);
  border: 0;
}

/* GitHub Dark style task lists */
.content-html .task-list-item {
  list-style-type: none;
}

.content-html .task-list-item-checkbox {
  margin: 0 8px 0 -20px;
  vertical-align: middle;
}

/* GitHub Dark style images */
.content-html img {
  max-width: 100%;
  box-sizing: border-box;
  background-color: var(--color-canvas-default);
}

/* Force all text to be white */
* {
  color: var(--color-fg-default);
}

/* Specific overrides for muted text */
.sidebar h2,
.content-html h6,
.content-html blockquote,
.language-label {
  color: var(--color-fg-muted) !important;
}

/* Links should stay blue */
.blog-title,
.sidebar-note a,
.content-html a {
  color: var(--color-accent-fg) !important;
}

/* ===== Responsive Styles ===== */
@media (max-width: 768px) {
  .container {
    padding: 16px;
  }
  
  .layout {
    flex-direction: column;
    gap: 16px;
  }

  .sidebar {
    flex: 1;
    order: 2;
  }

  .main-content {
    order: 1;
  }

  .blog-content h1 {
    font-size: 24px;
  }

  .blog-content h2 {
    font-size: 20px;
  }
  
  .blog-content h3 {
    font-size: 18px;
  }

  .subsection h2 {
    font-size: 20px;
  }
}

@media (max-width: 480px) {
  .container {
    padding: 8px;
  }

  .blog-content h1 {
    font-size: 20px;
  }

  .blog-content h2 {
    font-size: 18px;
  }
  
  .content-html {
    font-size: 13px;
  }

  .content-html pre {
    font-size: 12px;
    padding: 12px;
  }
}
